import React, { useState, useContext, useEffect } from 'react';
import { Authorize } from "../../../MainComponents/DesktopComponent";
import { toast } from "react-toastify";
import _ from 'lodash';
import {
    Container,
    Divider,
    Dropdown,
    Grid,
    Header,
    Icon,
    Image,
    List,
    Menu,
    Segment,
    Visibility,
} from 'semantic-ui-react'

const menuStyle = {
    border: 'none',
    borderRadius: 0,
    boxShadow: 'none',
    marginBottom: '1em',
    marginTop: '4em',
    transition: 'box-shadow 0.5s ease, padding 0.5s ease',
}

const fixedMenuStyle = {
    backgroundColor: '#fff',
    border: '1px solid #ddd',
    boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)',
}

const overlayStyle = {
    float: 'left',
    margin: '0em 3em 1em 0em',
}

const fixedOverlayStyle = {
    ...overlayStyle,
    position: 'fixed',
    top: '80px',
    zIndex: 10,
}

const overlayMenuStyle = {
    position: 'relative',
    left: 0,
    transition: 'left 0.5s ease',
}

const fixedOverlayMenuStyle = {
    ...overlayMenuStyle,
    left: '800px',
}

const LeftImage = () => (
    <Image
        floated='left'
        size='medium'
        src='/images/wireframe/square-image.png'
        style={{ margin: '2em 2em 2em -4em' }}
    />
)

const RightImage = () => (
    <Image
        floated='right'
        size='medium'
        src='/images/wireframe/square-image.png'
        style={{ margin: '2em -4em 2em 2em' }}
    />
)

const Paragraph = () => (
    <p>
        {[
            'Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Vestibulum ',
            'tortor quam, feugiat vitae, ultricies eget, tempor sit amet, ante. Donec eu libero sit amet quam egestas ',
            'semper. Aenean ultricies mi vitae est. Mauris placerat eleifend leo. Quisque sit amet est et sapien ',
            'ullamcorper pharetra. Vestibulum erat wisi, condimentum sed, commodo vitae, ornare sit amet, wisi. Aenean ',
            'fermentum, elit eget tincidunt condimentum, eros ipsum rutrum orci, sagittis tempus lacus enim ac dui. ',
            'Donec non enim in turpis pulvinar facilisis. Ut felis. Praesent dapibus, neque id cursus faucibus, tortor ',
            'neque egestas augue, eu vulputate magna eros eu erat. Aliquam erat volutpat. Nam dui mi, tincidunt quis, ',
            'accumsan porttitor, facilisis luctus, metus',
        ].join('')}
    </p>
)

toast.configure();

const UserDashboard = () => {

    const setAuth = useContext(Authorize);
    const [userDetails, setUserDetails] = useState({ name: '', email: '' });
    const { name, email } = userDetails;

    const { states, setStates } = ({
        menuFixed: false,
        overlayFixed: false,
        overlayRect: ''
    });

    const handleOverlayRef = (c) => {
        if (!states.overlayRect) {
            setStates({ overlayRect: _.pick(c.getBoundingClientRect(), 'height', 'width') })
        }
    }

    const stickOverlay = () => setStates({ overlayFixed: true })

    const stickTopMenu = () => setStates({ menuFixed: true })

    const unStickOverlay = () => setStates({ overlayFixed: false })

    const unStickTopMenu = () => setStates({ menuFixed: false })


    async function getName() {
        try {
            // const body = { TX_USER_NAME, TX_USER_EMAIL, TX_USER_PASSWORD };
            const response = await fetch("http://localhost:5000/dashboard/", {
                method: "GET",
                headers: { jwtToken: localStorage.jwtToken }
            });

            const parseRes = await response.json()
            console.log("useDetails", parseRes);

            setUserDetails({
                name: parseRes.TX_USER_NAME,
                email: parseRes.TX_USER_EMAIL
            })

        } catch (error) {
            console.log(error.message);
        }
    }

    useEffect(() => {
        getName();
    }, [])


    const logout = (e) => {
        e.preventDefault();
        localStorage.removeItem("jwtToken");
        setAuth(false);
    }

    // const { menuFixed, overlayFixed, overlayRect } = states;

    return (
        <>
            <h1>HELLO {name}, how are you?,<br />
            your email id is {email}
            </h1>

            <button onClick={e => logout(e)}>Logout</button>



            <>
                <div>
                    {/* Heads up, style below isn't necessary for correct work of example, simply our docs defines other
            background color.
          */}
                    <style>
                        {`
          html, body {
            background: #fff;
          }
        `}
                    </style>

                    <Container text style={{ marginTop: '2em' }}>
                        <Header as='h1'>Sticky Example</Header>
                        <p>
                            This example shows how to use lazy loaded images, a sticky menu, and a simple text
                            container
          </p>
                    </Container>

                    {/* Attaching the top menu is a simple operation, we only switch `fixed` prop and add another style if it has
            gone beyond the scope of visibility
          */}
                    <Visibility
                        onBottomPassed={stickTopMenu}
                        onBottomVisible={unStickTopMenu}
                        once={false}
                    >
                        <Menu
                            borderless
                            fixed={states.menuFixed ? 'top' : undefined}
                            style={states.menuFixed ? fixedMenuStyle : menuStyle}
                        >
                            <Container text>
                                <Menu.Item>
                                    <Image size='mini' src='/logo.png' />
                                </Menu.Item>
                                <Menu.Item header>Project Name</Menu.Item>
                                <Menu.Item as='a'>Blog</Menu.Item>
                                <Menu.Item as='a'>Articles</Menu.Item>

                                <Menu.Menu position='right'>
                                    <Dropdown text='Dropdown' pointing className='link item'>
                                        <Dropdown.Menu>
                                            <Dropdown.Item>List Item</Dropdown.Item>
                                            <Dropdown.Item>List Item</Dropdown.Item>
                                            <Dropdown.Divider />
                                            <Dropdown.Header>Header Item</Dropdown.Header>
                                            <Dropdown.Item>
                                                <i className='dropdown icon' />
                                                <span className='text'>Submenu</span>
                                                <Dropdown.Menu>
                                                    <Dropdown.Item>List Item</Dropdown.Item>
                                                    <Dropdown.Item>List Item</Dropdown.Item>
                                                </Dropdown.Menu>
                                            </Dropdown.Item>
                                            <Dropdown.Item>List Item</Dropdown.Item>
                                        </Dropdown.Menu>
                                    </Dropdown>
                                </Menu.Menu>
                            </Container>
                        </Menu>
                    </Visibility>

                    <Container text>
                        {_.times(3, (i) => (
                            <Paragraph key={i} />
                        ))}

                        {/* Example with overlay menu is more complex, SUI simply clones all elements inside, but we should use a
              different approach.
              An empty Visibility element controls the need to change the fixing of element below, it also uses height
              and width params received from its ref for correct display.
            */}
                        <Visibility
                            offset={80}
                            once={false}
                            onTopPassed={stickOverlay}
                            onTopVisible={unStickOverlay}
                            style={states.overlayFixed ? { ...states.overlayStyle, ...states.overlayRect } : {}}
                        />

                        <div ref={handleOverlayRef} style={states.overlayFixed ? fixedOverlayStyle : overlayStyle}>
                            <Menu
                                icon='labeled'
                                style={states.overlayFixed ? fixedOverlayMenuStyle : overlayMenuStyle}
                                vertical
                            >
                                <Menu.Item>
                                    <Icon name='twitter' />
                Twitter
              </Menu.Item>

                                <Menu.Item>
                                    <Icon name='facebook' />
                Share
              </Menu.Item>

                                <Menu.Item>
                                    <Icon name='mail' />
                Email
              </Menu.Item>
                            </Menu>
                        </div>

                        {_.times(3, (i) => (
                            <Paragraph key={i} />
                        ))}
                        <LeftImage />

                        <Paragraph />
                        <RightImage />

                        {_.times(4, (i) => (
                            <Paragraph key={i} />
                        ))}
                        <LeftImage />

                        <Paragraph />
                        <RightImage />

                        {_.times(2, (i) => (
                            <Paragraph key={i} />
                        ))}
                    </Container>

                    <Segment inverted style={{ margin: '5em 0em 0em', padding: '5em 0em' }} vertical>
                        <Container textAlign='center'>
                            <Grid columns={4} divided stackable inverted>
                                <Grid.Row>
                                    <Grid.Column>
                                        <Header inverted as='h4' content='Group 1' />
                                        <List link inverted>
                                            <List.Item as='a'>Link One</List.Item>
                                            <List.Item as='a'>Link Two</List.Item>
                                            <List.Item as='a'>Link Three</List.Item>
                                            <List.Item as='a'>Link Four</List.Item>
                                        </List>
                                    </Grid.Column>
                                    <Grid.Column>
                                        <Header inverted as='h4' content='Group 2' />
                                        <List link inverted>
                                            <List.Item as='a'>Link One</List.Item>
                                            <List.Item as='a'>Link Two</List.Item>
                                            <List.Item as='a'>Link Three</List.Item>
                                            <List.Item as='a'>Link Four</List.Item>
                                        </List>
                                    </Grid.Column>
                                    <Grid.Column>
                                        <Header inverted as='h4' content='Group 3' />
                                        <List link inverted>
                                            <List.Item as='a'>Link One</List.Item>
                                            <List.Item as='a'>Link Two</List.Item>
                                            <List.Item as='a'>Link Three</List.Item>
                                            <List.Item as='a'>Link Four</List.Item>
                                        </List>
                                    </Grid.Column>
                                    <Grid.Column>
                                        <Header inverted as='h4' content='Footer Header' />
                                        <p>
                                            Extra space for a call to action inside the footer that could help re-engage
                                            users.
                  </p>
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                            <Divider inverted section />
                            <Image src='/logo.png' centered size='mini' />
                            <List horizontal inverted divided link size='small'>
                                <List.Item as='a' href='#'>
                                    Site Map
              </List.Item>
                                <List.Item as='a' href='#'>
                                    Contact Us
              </List.Item>
                                <List.Item as='a' href='#'>
                                    Terms and Conditions
              </List.Item>
                                <List.Item as='a' href='#'>
                                    Privacy Policy
              </List.Item>
                            </List>
                        </Container>
                    </Segment>
                </div>
    )
  }
}
            </>

        </>
    )
}

export default UserDashboard;
