import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List
} from 'semantic-ui-react';
import RegistrationMultiStepForm from './RegistrationComponents/_RegistrationMultiStepForm';
import { Authorize } from "../../MainComponents/DesktopComponent";
import axios from '../../Store/_AxiosInstance';



const _RegistrationModal = props => {
    const [open, setOpen] = useState(true);
    const [defHeight, setHeight] = useState(window.innerWidth);
    const [ModalLogin, setModalLogin] = useState(true);
    const [ModalConsellorTypeSingup, setModalConsellorTypeSingup] = useState(false);
    const [ModalStudentSignUp, setModalStudentSignUp] = useState(false);
    const [inputs, setInputs] = useState({
        USER_NAME:"pig",
        USER_EMAIL : "",
        USER_PASSWORD: "",
        USER_CONFIRM_PASSWORD:""
    })

    const { USER_NAME, USER_EMAIL, USER_PASSWORD, USER_CONFIRM_PASSWORD } = inputs;

    const onChangeOfForm = (e) =>{
        console.log(e)
        setInputs({...inputs, [e.target.name] : e.target.value})
    };

    const onSubmitForm = async(e) => {
        e.prevent.default();

        try {
            const body = { USER_NAME, USER_EMAIL, USER_PASSWORD };
            const response = await fetch("http://localhost:5000/auth/register", {
                method:"POST",
                headers:{"Content-Type" : "application/json"},
                body: JSON.stringify(body)
            });
           
            const  parseRes  = await response.json()
            console.log(parseRes);
            
        } catch (error) {
            console.log(error.message);
        }
    }


    useEffect(() => {
        const setHeightofScreen = () => setHeight(window.innerHeight)
        window.addEventListener('resize', setHeightofScreen)

        return () => {
            window.removeEventListener('resize', setHeightofScreen)
        }
    })

    const StudentSignUp = () => {
        setModalLogin(false)
        setModalStudentSignUp(true)
    }
    const CounsellorSignUp = () => {
        setModalLogin(false)
        setModalConsellorTypeSingup(true)
    }

    const close = () => {
        setOpen(false);
        props.onCloseModal();
    }

    const setAuth = useContext(Authorize);

    return (
        <React.Fragment>
            {ModalLogin &&
                <>
                    <Modal
                        closeIcon
                        size={'small'}
                        closeOnEscape={false}
                        closeOnDimmerClick={false}
                        dimmer="blurring"
                        open={open}
                        onClose={() => close()}>

                        <Modal.Content>
                            <Grid textAlign='center' verticalAlign='middle'>
                                <Grid.Column style={{ maxWidth: 450 }}>
                                    <Header as='h2' color='black' textAlign='center' style={{ padding: "10px" }}>
                                        Log-in
                            </Header>
                                    <Form size='large'>
                                        <List divided relaxed>
                                            <List.Item>
                                                <Button color='SingPass'>
                                                    <Icon name='SingPass' /> Log In with SingPass
                                    </Button> &nbsp;&nbsp;&nbsp;

                                        <Button color='facebook'>
                                                    <Icon name='facebook' /> Log In with Facebook
                                    </Button>
                                            </List.Item>
                                            <List.Item>
                                                <Button color='red'>
                                                    <Icon name='google' /> Log In with Google &nbsp;&nbsp;
                                    </Button> &nbsp;&nbsp;&nbsp;
                                    <Button color='linkedin'>
                                                    <Icon name='linkedin' /> Log In with LinkedIn &nbsp;&nbsp;
                                    </Button>
                                            </List.Item>
                                        </List>
                                    </Form>
                                    <br />
                                    <Divider horizontal>Or</Divider>
                                    <Form size='small'>
                                        <Segment stacked>
                                            <Form.Input fluid icon='user' iconPosition='left' placeholder='E-mail address' />
                                            <Form.Input
                                                fluid
                                                icon='lock'
                                                iconPosition='left'
                                                placeholder='Password'
                                                type='password'
                                            />
                                            <Button color='teal' fluid size='large'>
                                                Login
                                </Button>
                                        </Segment>
                                    </Form>

                                    <Message>
                                        New to us? <br />
                                        <span onClick={StudentSignUp}>
                                            <a href='#'>Sign Up as a Student</a>&nbsp;&nbsp;
                                        </span > |
                                        <span onClick={CounsellorSignUp}>&nbsp;&nbsp;<a href='#'>Sign Up as a Counsellor</a></span>
                                    </Message>

                                </Grid.Column>
                            </Grid>
                        </Modal.Content>
                        <br /><br />
                    </Modal>
                </>
            }

            {ModalConsellorTypeSingup &&
                <>
                    <Modal
                        closeIcon
                        size="large"
                        centered={false}
                        open={open}
                        onClose={() => close()}
                        closeOnEscape={false}
                        closeOnDimmerClick={false}
                    >
                        <Modal.Content scrolling>
                            <Grid>
                                <Grid.Column style={{ maxWidth: '100%', backgroundColor: 'white' }}>
                                    <RegistrationMultiStepForm />
                                </Grid.Column>
                            </Grid>
                        </Modal.Content>
                    </Modal>
                </>
            }


            {ModalStudentSignUp &&
                <>
                    <Modal
                        closeIcon
                        size={'small'}
                        closeOnEscape={false}
                        closeOnDimmerClick={false}
                        dimmer="blurring"
                        open={open}
                        onClose={() => close()}>

                        <Modal.Content>
                            <Grid textAlign='center' verticalAlign='middle'>
                                <Grid.Column style={{ maxWidth: 450 }}>
                                    <Header as='h2' color='black' textAlign='center' style={{ padding: "10px" }}>
                                        Sign up for Students
                            </Header>
                                    <br />
                                    <Divider horizontal>Or</Divider>
                                    <Form size='small' onSubmit={onSubmitForm}>
                                        <Segment stacked>
                                            <Form.Group widths='equal'>

                                                <Form.Field className="CustomForm">
                                                    <Icon name="user" className="customIconsAlign" />
                                                        &nbsp;&nbsp;&nbsp;
                                                        <input
                                                        placeholder='Enter your Name'
                                                        type='text'
                                                        name={USER_NAME}
                                                        onChange={e => onChangeOfForm(e)}
                                                        value={USER_NAME}
                                                    />
                                                </Form.Field>
                                            </Form.Group>
                                            <Form.Group widths='equal'>
                                                <Form.Field className="CustomForm">
                                                    <Icon name="user" className="customIconsAlign" />
                                                        &nbsp;&nbsp;&nbsp;
                                                        <input
                                                        placeholder='Enter your Email Id'
                                                        type='text'
                                                        name={USER_EMAIL}
                                                        onChange={e => onChangeOfForm(e)}
                                                        value={USER_EMAIL}
                                                    />
                                                </Form.Field>
                                            </Form.Group>
                                            <Form.Group widths='equal'>
                                                <Form.Field className="CustomForm">
                                                    <Icon name="user" className="customIconsAlign" />
                                                        &nbsp;&nbsp;&nbsp;
                                                        <input
                                                        placeholder='Enter password'
                                                        type='password'
                                                        name={USER_PASSWORD}
                                                        onChange={e => onChangeOfForm(e)}
                                                        value={USER_PASSWORD}
                                                    />
                                                </Form.Field>
                                            </Form.Group>
                                            <Form.Group widths='equal'>
                                                <Form.Field className="CustomForm">
                                                    <Icon name="user" className="customIconsAlign" />
                                                        &nbsp;&nbsp;&nbsp;
                                                        <input
                                                        placeholder='Re-enter password'
                                                        type='password'
                                                        name={USER_CONFIRM_PASSWORD}
                                                        onChange={e => onChange(e)}
                                                        value={USER_CONFIRM_PASSWORD}
                                                    />
                                                </Form.Field>
                                            </Form.Group>


                                            {/* <Button onClick={() => setAuth(true)} color='teal' fluid size='large'> */}
                                            <Button color='teal' fluid size='large'>
                                                Sign up
                                            </Button>
                                        </Segment>
                                    </Form>
                                </Grid.Column>
                            </Grid>
                        </Modal.Content>
                        <br /><br />
                    </Modal>
                </>
            }


        </React.Fragment >
    )
}
export default _RegistrationModal;
